mapper.insert:

	SELECT  NVL(MAX(USER_NUM),0)+1 AS "USER_NUM" FROM USER_INFO

	INSERT INTO USER_INFO 
		(
			USER_NUM
		   ,USER_ID
		   ,USER_PW
		   ,USER_NAME
		   ,USER_PHONE
		   ,USER_ADDR1
		   ,USER_ADDR2
		   ,CREATE_TIME
		   ,MODIFIED_TIME
		) VALUES 
		(
		    #{userNum}
		   ,#{userId}
		   ,#{userPw}
		   ,#{userName}
		   ,#{userPhone}
		   ,#{userAddr1}
		   ,#{userAddr2}
		   ,sysdate
		   ,null
		)

mapper.select:
SELECT COUNT(*)
FROM USER_INFO 
WHERE USER_ID = #{userId}

SELECT USER_NAME
FROM USER_INFO
WHERE USER_ID = #{userId} 
AND   USER_PW = #{userPw}
